import React, { Component } from "react";
import Songs from "./components/Songs.js";
import { async } from "q";

const API_KEY = "e125487ee7c050f4e926331f871841ee";

class App extends Component {
  state = {
    songs: []
  };

  componentDidMount = async () => {
    const api_call = await fetch(
      `http://ws.audioscrobbler.com/2.0/?method=geo.gettoptracks&country=india&api_key=${API_KEY}&format=json`
    );

    const data = await api_call.json();
    console.log(data);
    this.setState({
      songs: data.tracks.track
    });
  };

  render() {
    return <Songs songs={this.state.songs} />;
  }
}

export default App;
