import React, { Component } from "react";
import Songs from "./components/Songs.js";
import Form from "./components/Form.js";
import Heading from "./components/Heading.js";
import { async } from "q";

const API_KEY = "e125487ee7c050f4e926331f871841ee";

class App extends Component {
  state = {
    songs: [],
    country: ""
  };

  getSongs = async e => {
    const country = e.target.elements.country.value;
    e.preventDefault();
    const api_call = await fetch(
      `http://ws.audioscrobbler.com/2.0/?method=geo.gettoptracks&country=${country}&api_key=${API_KEY}&format=json`
    );
    const data = await api_call.json();
    this.setState({
      songs: data.tracks.track,
      country: country
    });
  };

  componentDidMount = async () => {
    const api_call = await fetch(
      `http://ws.audioscrobbler.com/2.0/?method=geo.gettoptracks&country=india&api_key=${API_KEY}&format=json`
    );

    const data = await api_call.json();
    console.log(data);
    this.setState({
      songs: data.tracks.track,
      country: "india"
    });
  };

  render() {
    return (
      <div style={{ marginTop: "5%" }}>
        <Heading country={this.state.country} />
        <Form getSongs={this.getSongs} />
        <Songs songs={this.state.songs} />
      </div>
    );
  }
}

export default App;
